const express = require("express");
const multer = require("multer");
const cors = require("cors");
const { exec } = require("child_process");
const path = require("path");

const app = express();
app.use(cors());
app.use(express.json());
app.use("/uploads", express.static(path.join(__dirname, "uploads")));

const storage = multer.diskStorage({
  destination: (req, file, cb) => cb(null, "uploads/"),
  filename: (req, file, cb) => cb(null, Date.now() + "-" + file.originalname),
});
const upload = multer({ storage });

app.post("/api/hologram", upload.single("photo"), (req, res) => {
  const photoPath = req.file.path;
  exec(`python hologram.py "${photoPath}"`, (err) => {
    if (err) return res.status(500).json({ error: "AI generation failed" });
    const gifUrl = `/uploads/${path.basename(photoPath, path.extname(photoPath))}.gif`;
    res.json({ gifUrl });
  });
});

app.listen(5000, () => console.log("ðŸš€ Server running on http://localhost:5000"));
